import { createStore } from "vuex";
import axios from "axios";

export default createStore({
  namespaced: true,
  state: {
    categorie: [],
  },
  getters: {
    getcategories(state) {

      data: state => state.data
    },
  },
  mutations: {
    SET_CATEGORIES(state, data) {
      state.categorie = data;
    },
  },
  actions: {
    async getCat({ commit }) {
      await axios
        .get("http://localhost:8081/wp-json/wp/v2/categories", {
          headers: {
            Authorization:
              "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJodHRwOi8vbG9jYWxob3N0OjgwODEiLCJpYXQiOjE3Mzc0NTA1NDAsIm5iZiI6MTczNzQ1MDU0MCwiZXhwIjoxNzM3NDUxMTQwLCJkYXRhIjp7InVzZXIiOnsiaWQiOjEsImRldmljZSI6IiIsInBhc3MiOiIzYWJjZmRiYzNiYjVhNjg1YmZlYzc5NDM0ZjdkNjVmOSJ9fX0.NKW1sYBxOm-frAycEzwlUVXMqoWlUJByW9rS1Ugt5wQ",
          },
        })
        .then((res) => {
          commit("SET_CATEGORIES", res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    },
  },
});
