import axios from "axios";

const postModule = {
  namespaced: true,
  state: {
    posts: [],
  },
  getters: {
    allpost: (state) => state.posts,
  },
  mutations: {
    SET_POSTS(state, data) {
      state.posts = data;
    },
  },
  actions: {
    async getPost({ commit }) {
      await axios
        .get("http://localhost:8081/wp-json/wp/v2/posts?page=1&per_page=50", {
          headers: {
            Authorization:
              "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJodHRwOi8vbG9jYWxob3N0OjgwODEiLCJpYXQiOjE3Mzc1NjU0NzcsIm5iZiI6MTczNzU2NTQ3NywiZXhwIjoxNzM4MTcwMjc3LCJkYXRhIjp7InVzZXIiOnsiaWQiOiIxIn19fQ.w_pJRjFImG89mq0u5Ny8Sc3fHOm3jIsyV8Re26ql68o",
          },
        })
        .then((res) => {
          commit("SET_POSTS", res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    },
  },
};

export default postModule;
